//import thu vien
import React from "react";
import "./Review.css";
import ReviewCity from "./ReviewCity";
import { useCallback, useEffect, useState } from "react";
import ReviewHotel from "./ReviewHotel";
import ReviewType from "./ReviewType";
//Tao component review voi 3 phan - review city, review type va review hotel
const Review = (props) => {
  const [data, setData] = useState();
  console.log("aaaaaaaaaaaaaaaaaaaaaaa");
  const hotelData = useCallback(async () => {
    try {
      const result = await fetch(`http://localhost:4000`);
      // const list = await result.json();
      const list = await result.json();
      console.log(list);  
      setData(list.results);
      console.log(data);

      if (!!list.message) console.log(list.message);
    } catch(err){
      throw Error(err);
    }
  }, []);

  useEffect(() => {
    hotelData();
  }, [hotelData]);
  console.log(data);

  let countDN = 0;
  let countHN = 0;
  let countHCM = 0;
  data.map((hotel) => {
    if (data.city === "Ha Noi") countHN++;
    else if (data.city === "Ho Chi Minh") countHCM++;
    else if (data.city === "Da Nang") countDN++;
  });

  return (
    <div className='Review'>
      {/* Div review city, tao layout flex voi moi phan tu la mot phan tu city trong data */}
      <div className='Review_content'>
        {/* {props.citydatas.map((expense) => (
        // {data.map((expense) => (
          // <NavBarItem/>
          <ReviewCity
            key={expense.name}
            name={expense.name}
            subText={expense.subText}
            image={expense.image}
          />
        ))} */}
         <ReviewCity
            key="Ha Noi"
            name={expense.name}
            subText={expense.subText}
            image={expense.image}
          />
           <ReviewCity
            key={expense.name}
            name={expense.name}
            subText={expense.subText}
            image={expense.image}
          />
           <ReviewCity
            key={expense.name}
            name={expense.name}
            subText={expense.subText}
            image={expense.image}
          />
      </div>
      <h3 className='Review_content'>Browse by property type </h3>
      {/* Div review type, tao layout flex voi moi phan tu la mot phan tu type trong data */}
      <div className='Review_content'>
        {props.typedata.map((expense) => (
          // <NavBarItem/>
          <ReviewType
            key={expense.name}
            name={expense.name}
            count={expense.count}
            image={expense.image}
          />
        ))}
      </div>

      <h3 className='Review_content'>Homes Guest Loves</h3>
      {/* Div review hotel, tao layout flex voi moi phan tu la mot phan tu hotel trong data */}
      <div className='Review_content'>
        {props.hoteldata.map((expense) => (
          <ReviewHotel
            key={expense.name}
            name={expense.name}
            city={expense.city}
            price={expense.price}
            rate={expense.rate}
            type={expense.type}
            image_url={expense.image_url}
          />
        ))}
      </div>
    </div>
  );
};
export default Review;
