// import image from  "../../City Image"

import { Navigate, Route, Router } from "react-router-dom";

//tao dummy data cua trang REVIEW, gom thong tin tu cac json navbar, city,hotel,type va footer truyen tu props


const Home = (props) => {

	const [data, setData] = useState();
  const hotelData = useCallback(async () => {
    try {
      const result = await fetch(`http://localhost:4000`);
      // const list = await result.json();
      const list = await result.json();
      console.log(list.results);  
      setData(list.results);

      if (!!list.message) console.log(list.message);
    } catch(err){
      throw Error(err);
    }
  }, []);

  useEffect(() => {
    hotelData();
  }, [hotelData]);
  console.log(data);

  let countDN = 0;
  let countHN = 0;
  let countHCM = 0;
  data.map((hotel) => {
    if (hotel.city === "Ha Noi") countHN++;
    else if (hotel.city === "Ho Chi Minh") countHCM++;
    else if (hotel.city === "Da Nang") countDN++;	
  });
	console.log(countDN);
	const City_Data =[
		{
