//import thu vien
import React from "react";
import "./Review.css";
import ReviewCity from "./ReviewCity";
import { useCallback, useEffect, useState } from "react";
import ReviewHotel from "./ReviewHotel";
import ReviewType from "./ReviewType";
//Tao component review voi 3 phan - review city, review type va review hotel



const Type_Data =[
	{
		"name": "Hotels",
		"count": 233,
		"image": "./images/type_1.webp"
	},
	{
		"name": "Apartments",
		"count": 2331,
		"image": "./images/type_2.jpg"
	},
	{
		"name": "Resorts",
		"count": 2331,
		"image": "./images/type_3.jpg"
	},
	{
		"name": "Villas",
		"count": 2331,
		"image": "./images/type_4.jpg"
	},
	{
		"name": "Cabins",
		"count": 2331,
		"image": "./images/type_5.jpg"
	}
]
const Hotel_Data =[
	{
		"name": "Aparthotel Stare Miasto",
		"city": "Madrid",
		"price": 120,
		"rate": 8.9,
		"type": "Excellent",
		"image_url": "./images/hotel_1.webp"
	},
	{
		"name": "Comfort Suites Airport",
		"city": "Austin",
		"price": 140,
		"rate": 9.3,
		"type": "Exceptional",
		"image_url": "./images/hotel_2.jpg"
	},
	{
		"name": "Four Seasons Hotel",
		"city": "Lisbon",
		"price": 99,
		"rate": 8.8,
		"type": "Excellent",
		"image_url": "./images/hotel_3.jpg"
	},
	{
		"name": "Hilton Garden Inn",
		"city": "Berlin",
		"price": 105,
		"rate": 8.9,
		"type": "Excellent",
		"image_url": "./images/hotel_4.jpg"
	}
]

const Review = (props) => {

  return (
    <div className='Review'>
      {/* Div review city, tao layout flex voi moi phan tu la mot phan tu city trong data */}
      <div className='Review_content'>
        {props.citydatas.map((expense) => (
        // {data.map((expense) => (
          // <NavBarItem/>
          <ReviewCity
            key={expense.name}
            name={expense.name}
            subText={expense.subText}
            image={expense.image}
          />
        ))}
      </div>
      <h3 className='Review_content'>Browse by property type </h3>
      {/* Div review type, tao layout flex voi moi phan tu la mot phan tu type trong data */}
      <div className='Review_content'>
        {Type_Data.map((expense) => (
          // <NavBarItem/>
          <ReviewType
            key={expense.name}
            name={expense.name}
            count={expense.count}
            image={expense.image}
          />
        ))}
      </div>

      <h3 className='Review_content'>Homes Guest Loves</h3>
      {/* Div review hotel, tao layout flex voi moi phan tu la mot phan tu hotel trong data */}
      <div className='Review_content'>
        {Hotel_Data.map((expense) => (
          <ReviewHotel
            key={expense.name}
            name={expense.name}
            city={expense.city}
            price={expense.price}
            rate={expense.rate}
            type={expense.type}
            image_url={expense.image_url}
          />
        ))}
      </div>
    </div>
  );
};
export default Review;
